const months=["січня","лютого","березня","квітня","травня","червня","липня","серпня","вересня","жовтня","листопада","грудня"],dayNames=["Неділя","Понеділок","Вівторок","Середа","Четверг","Пятниця","Субота"],correctNumber=e=>e<10?`0${e}`:e,getUserTime=e=>{const t=correctNumber(e.getDate()),r=months[e.getMonth()],o=dayNames[e.getDay()];return`${t} ${r} ${e.getFullYear()} ${correctNumber(e.getHours())}:${correctNumber(e.getMinutes())}:${correctNumber(e.getSeconds())} (${o})`};console.log(getUserTime(new Date));